(5, 1, 'Условия соглашения', '&lt;p&gt;\r\n	Условия соглашения&lt;/p&gt;\r\n', '', '', '', ''),
(3, 1, 'Скидка', '&lt;p&gt;\r\n	Скидка&lt;/p&gt;\r\n', '', '', '', ''),
